class Command(BaseCommand):

    help = 'pulls all gitcoin steward metadata info'

    def handle(self, *args, **options):
        web3 = get_web3('mainnet')
        gtc = get_gtc_contract(web3)
        print("votes_delegated, handle")
        for gtcs in GTCSteward.objects.all():
            try:
                votes = gtc.functions.getCurrentVotes(Web3.toChecksumAddress(gtcs.gtc_address)).call()
                delegates = gtc.functions.delegates(Web3.toChecksumAddress(gtcs.gtc_address)).call()
                votes = round(votes / 10 **18)
                print(f"{votes},{gtcs.profile.handle}")
            except Exception as e:
                pass;

